SELECT DISTINCT StatusOfFunds.BFY, StatusOfFunds.EFY, StatusOfFunds.FundCode, StatusOfFunds.FundName, StatusOfFunds.RpioCode, StatusOfFunds.RpioName, StatusOfFunds.AhCode, StatusOfFunds.AhName, StatusOfFunds.OrgCode, StatusOfFunds.OrgName, StatusOfFunds.AccountCode, StatusOfFunds.ProgramProjectName, StatusOfFunds.BocCode, StatusOfFunds.BocName, Format(Sum(IIf(StatusOfFunds.OpenCommitments>0 And StatusOfFunds.Amount>0,Round(StatusOfFunds.OpenCommitments/StatusOfFunds.Amount,2),0)),"Percent") AS Committed, Format(Sum(IIf(StatusOfFunds.Obligations>0 And StatusOfFunds.Amount>0,Round(StatusOfFunds.Obligations/StatusOfFunds.Amount,2),0)),"Percent") AS Obligated, Format(Sum(IIf(StatusOfFunds.Used>0 And StatusOfFunds.Amount>0,Round(StatusOfFunds.Used/StatusOfFunds.Amount,2),0)),"Percent") AS Utilization, Format(Sum(IIf(StatusOfFunds.Available>0 And StatusOfFunds.Amount>0,Round(StatusOfFunds.Available/StatusOfFunds.Amount,2),0)),"Percent") AS Availability, Format(Sum(IIf(StatusOfFunds.Expenditures>0 And StatusOfFunds.Amount>0,Round(StatusOfFunds.Expenditures/StatusOfFunds.Amount,2),0)),"Percent") AS Outlaid
FROM StatusOfFunds
GROUP BY StatusOfFunds.BFY, StatusOfFunds.EFY, StatusOfFunds.FundCode, StatusOfFunds.FundName, StatusOfFunds.RpioCode, StatusOfFunds.RpioName, StatusOfFunds.AhCode, StatusOfFunds.AhName, StatusOfFunds.OrgCode, StatusOfFunds.OrgName, StatusOfFunds.AccountCode, StatusOfFunds.ProgramProjectName, StatusOfFunds.BocCode, StatusOfFunds.BocName, StatusOfFunds.Amount
HAVING (((StatusOfFunds.RpioCode) Not In ("92","94","95")) AND ((StatusOfFunds.BocCode)<>"10") AND ((StatusOfFunds.Amount)>0))
ORDER BY StatusOfFunds.BFY DESC;